import React, {useState} from 'react';
import { View, TextInput, FlatList, StyleSheet, Modal } from 'react-native';
import { Text, Card, Button } from 'react-native-elements';
import Userpost from './Userpost';
import Profile from './Profile';

export default function Library(props) {

    const [posts, setPosts] = useState([]);
    const [text, setText] = useState("");
    const [genre, setGenre] = useState("");
    const [count, setCount] = useState(0);

    const [isModalVisible, setModalVisible] = useState(false);

    function remove(id) {
        let tempArray = posts.filter((item) => {
            return item.id !== id
        });
        setPosts(tempArray);
    }



  return (
    <View style={styles.container}>
          <Button title="Add Books" onPress={() => setModalVisible(true)} />
            <Modal
                animationType="slide"
                transparent={true}
                visible={isModalVisible}
                onRequestClose={() => {
                    setModalVisible(!isModalVisible);
                }} >
              <TextInput
                  placeholder="Book Title"
                  onChangeText={setText} />
              <TextInput
                  placeholder="Genre"
                  onChangeText={setGenre} />
          <Button title="Submit"
              onPress={() => {
                  setPosts(posts.concat({
                      id: count,
                      text: text,
                      genre: genre,
                  }));
                  setCount(count + 1);
                  setModalVisible(false);
          }}></Button>
            </Modal>
        <FlatList
            data={posts}

              renderItem={
                  ({ item }) => {
                      return (
                          <Card>
                              <Card.Title style={styles.modalStyling}>Book Title: {item.text}</Card.Title>
                             <Text> Genre: <Userpost text={item.genre} remove={remove} id={item.id}/> </Text>
                        </Card>
                    )
                }
            }
        />

          <Button
              title="Go to Profile"
              onPress={() => props.navigation.navigate("Profile", {genre: genre, text: text,})}> </Button>

    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    backgroundColor: '#ecf0f1',
    padding: 8,
    },
    modalStyling: {
        margin: 25
    }
});
