import React, { useState } from 'react';
import { StyleSheet, View} from 'react-native';
import {Card, Text, Button} from 'react-native-elements';

export default function Community(props) { //this element has two attributes, name and update

    const [like, setLike] = useState(6); //own internal state
    const [dislike, setDislike] = useState(-20);


	return (

      <Card> 
        <Card.Title>@booksrthebst</Card.Title> 
            <Text>Six of Crows is ok, don't really get the hype behind it</Text>
            <View style={styles.fixToText}>
                <Button title="Like" onPress={() => {
                    if(setDislike = -21)
                    setLike(like + 1); //increases the local counter

				}
				}></Button>
                <Text>{like}</Text>

                <Button title="Dislike" onPress={() => {
                    if(setLike > 6)
                    setDislike(dislike - 1)
				}
				}></Button>
                <Text>{dislike}</Text>
            </View>
      </Card>
);

}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    marginHorizontal: 16,
  },

  fixToText: {
    flexDirection: 'row',
    justifyContent: 'space-between',
  },

});
