import React, {useState} from 'react';
import {View, Text, Button, TextInput, StyleSheet} from 'react-native'

export default function Profile(props) {


    const [displayText, setDisplayText] = useState("");
    const [submittedText, setSubmittedText] = useState('');


    function changeText(enteredText) {
        setDisplayText(enteredText);
    }

    function makeBlank() {
        setSubmittedText(displayText);
        setDisplayText('');
    }

    return (
        <View style={styles.container}>
            <Text style={styles.larger}>Current Username: {submittedText}</Text>
            <Text>Input New Username Below</Text>
            <TextInput placeholder style={styles.textBox} onChangeText={changeText} value={displayText} />

            <Button title="Change" onPress={makeBlank}></Button>


            <Button title="Go to Home" onPress={() => props.navigation.navigate("Home", {
            username: submittedText,
            })}> </Button>
        </View>
    )
}

const styles = StyleSheet.create({
  larger: {
    fontSize: 20,
    },
    textBox: {
        paddingTop: 5,
        paddingLeft: 5,
        paddingBottom: 15,
        fontSize: 15,
    },
    container: {
        padding: 5,

    }
});
