import React, {useState} from 'react';
import { View, TextInput, StyleSheet, Modal, Text } from 'react-native';
import { ListItem, Button} from 'react-native-elements';
import Userpost from './Userpost';

export default function Library() {
    const [isModalVisible, setModalVisible] = useState(false);
    const [bookTitle, setBookTitle] = useState([]);



    return (
        <View style={styles.centeredView}>
            <Button title="Add Books" onPress={() => setModalVisible(true)}  />
            <Modal
                animationType="slide"
                transparent={true}
                visible={isModalVisible}
                onRequestClose={() => {
                    setModalVisible(!isModalVisible);
                }} >
                <View style={styles.centeredView}>
                    <View style={styles.modalView}>
                        <TextInput
                                placeholder="Book Title"
                                value={bookTitle}
                                onChangeText={(value) => setBookTitle(value)} />
                        <Button title="Submit" onPress={() => setModalVisible(false) } />

                    </View>
                    </View>
            </Modal>
            <Text>{boo}</Text>
            </View>
    );
}


const styles = StyleSheet.create({
  centeredView: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    marginTop: 22
  },
  modalView: {
    margin: 20,
    backgroundColor: "white",
    borderRadius: 20,
    padding: 35,
    alignItems: "center",
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2
    },
    shadowOpacity: 0.25,
    shadowRadius: 4,
    elevation: 5
  },
  button: {
    borderRadius: 20,
    padding: 10,
    elevation: 2
  },
  buttonOpen: {
    backgroundColor: "#F194FF",
  },
  buttonClose: {
    backgroundColor: "#2196F3",
  },
  textStyle: {
    color: "white",
    fontWeight: "bold",
    textAlign: "center"
  },
  modalText: {
    marginBottom: 15,
    textAlign: "center"
  }
});
