import React, {useState} from 'react';
import {Card, Text, Button} from 'react-native-elements';

export default function Community(props) { //this element has two attributes, name and update

    const [counter, setCounter] = useState(0); //own internal state

	return (

      <Card> 
        <Card.Title>@booksrthebst</Card.Title> 
            <Text>Six of Crows is ok, don't really get the hype behind it</Text>
            <View >
        <Button title="Like" onPress={() => {
					setCounter(counter + 1); //increases the local counter
				}
				}></Button>

            <Button title="Dislike" onPress={() => {
                    setCounter(counter - 1)
				}
				}></Button>
            </View>
      </Card>
);

}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    marginHorizontal: 16,
  },
  title: {
    textAlign: 'center',
    marginVertical: 8,
  },
  fixToText: {
    flexDirection: 'row',
    justifyContent: 'space-between',
  },
  separator: {
    marginVertical: 8,
    borderBottomColor: '#737373',
    borderBottomWidth: StyleSheet.hairlineWidth,
  },
});
