import React, {useState} from 'react';
import { View, TextInput, StyleSheet, Modal } from 'react-native';
import { ListItem, Button} from 'react-native-elements';
import Userpost from './Userpost';

export default function Library() {
    const [isModalVisible, setModalVisible] = useState(false);
    const [bookTitle, setBookTitle] = useState("");



    // Create an array of data.
  // Each entry will have an 'index'.
  const [examples, setExamples] = useState([]);
  // Separately track unique ids
  // Increase the number each time an entry is created
  const [id, setId] = useState(0);

  // Define a removal function
  const remove = (index) => {
    // Look for all entries where the index IS NOT equal to the passed one
    let tempArray = examples.filter(entry => entry.index != index);
    // Update the internal state
    setExamples(tempArray);
  };

    function doStuff() {
        setModalVisible(false);
}
    return (
        <View style={styles.centeredView}>
            <Button title="Add Books" onPress={() => setModalVisible(true)}  />
            <Modal
                animationType="slide"
                transparent={true}
                visible={isModalVisible}
                onRequestClose={() => {
                    setModalVisible(!isModalVisible);
                }} >
                <View style={styles.centeredView}>
                    <View style={styles.modalView}>
                        <TextInput
                                placeholder="Book Title"
                                value={bookTitle}
                                onChangeText={(value) => setBookTitle(value)} />
                        <Button title="Submit" onPress={() =>
                            setModalVisible(false)

                        } />

      {
        examples.map((entry) => {
          return <Example index={entry.index} remove={remove} key={entry.index} />
        })
      }
                    </View>
                    </View>
            </Modal>
            </View>
    );
}


const styles = StyleSheet.create({
  centeredView: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    marginTop: 22
  },
  modalView: {
    margin: 20,
    backgroundColor: "white",
    borderRadius: 20,
    padding: 35,
    alignItems: "center",
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2
    },
    shadowOpacity: 0.25,
    shadowRadius: 4,
    elevation: 5
  },
  button: {
    borderRadius: 20,
    padding: 10,
    elevation: 2
  },
  buttonOpen: {
    backgroundColor: "#F194FF",
  },
  buttonClose: {
    backgroundColor: "#2196F3",
  },
  textStyle: {
    color: "white",
    fontWeight: "bold",
    textAlign: "center"
  },
  modalText: {
    marginBottom: 15,
    textAlign: "center"
  }
});
