import {useState} from 'react';
import {Card} from 'react-native-elements';

export default function MarketplaceItem(props) { //this element has two attributes, name and update
	const [counter,setCounter] = useState(0); //own internal state

	return (

      <Card> 
        <Card.Title>Title!</Card.Title> 
        <Text>{props.name}</Text>
        <Text> Count: {counter} </Text> //current value of counter
        <Button title="Click me!" onPress={() => {
					setCounter(counter + 1); //increases the local counter
					props.update(); //calling update function.
				}
				}></Button>
 <Button title="Remove!" onPress={() => {
					props.remove(); //calling remove function.
				}
				}></Button>
      </Card>
);

}
