import React, {useState} from 'react';
import { Text, View, TextInput, FlatList, Modal } from 'react-native';
import { ListItem, Button} from 'react-native-elements';
import Userpost from './Userpost';

export default function Library() {
    const [isModalVisible, setModalVisible] = useState(false);
    //const [bookTitle, setBookTitle] = useState("");
     const [posts, setPosts] = useState([]);
    const [text, setText] = useState("");
    const toggleModalVisibility = () => {
        setModalVisible(!isModalVisible);
    }
    return (
        <View>
               <FlatList
                data={posts}
                renderItem={
                    ({item})=> {
                        return (
                            <View >
                                      <Button title="Add Books" onPress={toggleModalVisibility} />

            <Modal animationType="slide"
                transparent visible={isModalVisible}
                presentationStyle="overFullScreen"
                onDismiss={toggleModalVisibility} >
                <View>
                    <View>
                        <TextInput>
                            placeholder="Book Title"
                            value={posts}
                            onChangeText={(value) => setPosts(value)}
                        </TextInput>
                    </View>
                    </View>
                                </Modal>
                            </View>
                        )
                    }
                }
            />
            <Text>Add Books</Text>
            <Button title="Add" onPress={toggleModalVisibility} />
            <TextInput onChangeText={setText} />
            <Button type="outline" title="Submit" onPress={() => {
                setPosts(posts.concat({
                    text: text
                }));
            }} />
        </View>
    )
}
