import React, {useState} from 'react';
import {Card, Text, Button} from 'react-native-elements';

export default function Community(props) { //this element has two attributes, name and update

    const [counter, setCounter] = useState(0); //own internal state
    const[itemArray, setItemArray] = useState([]);



    function update() {

	setCounter(count + 1);
}

function remove(id){ // will build a new array. Each time it checks if filter applies and then will create a new array
	setitemArray(itemArray.filter((entry) => { // creating a new array and something to state each time
			return id != entry.id
		}));
}

	return (

      <Card> 
        <Card.Title>Title!</Card.Title> 
        <Text>{props.name}</Text>
        <Text> Count: {counter} </Text> 
        <Button title="Click me!" onPress={() => {
					setCounter(counter + 1); //increases the local counter
					props.update(); //calling update function.
				}
				}></Button>

 <Button title="Remove!" onPress={() => {
					props.remove(); //calling remove function.
				}
				}></Button>
      </Card>
);

}
